import actionTypes from "./actionTypes";

const createMinion = () => {
    return {
        type: actionTypes.CREATE_MINION
    }
}

const destroyMinion = () => {
    return {
        type: actionTypes.DESTROY_MINION
    }
}

const createTeam = (value) => {
    return {
        type: actionTypes.CREATE_TEAM,
        value: value
    }
}

const destroyTeam = (value) => {
    return {
        type: actionTypes.DESTROY_TEAM,
        value: value
    }
}

const save = (value) => {
    return {
        type: actionTypes.SAVE,
    }
}



export default createMinion;
