const initialState = {
    minions : 0
};

    // CREATE_MINION
    // DESTROY_MINION
    // CREATE_TEAM
    // DESTROY_TEAM

    const reducer = (state = initialState, action) => {

        //Actions
        switch (action.type) {
            case 'CREATE_MINION' :
                return {
                minions : state.minions +1
                }
                case 'DESTROY_MINION' :
                    return {
                    minions : state.minions -1
                    }
                    case 'CREATE_TEAM' :
                        return {
                        minions : state.minions + action
                        }
                        case 'DESTROY_TEAM' :
                            return {
                            minions : state.minions - 5
                            }


            default:
                return state;
    } 
};

export default reducer;
